vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x) + iTime * 0.5; // Add rotation over time

    // Create hexagonal pattern using polar coordinates
    a += sin(r * 6.0 + iTime) * 0.1; // Add some distortion
    float hex = abs(sin(a * 3.0)) * 0.5 + 0.5;

    // Generate noise for color shifting
    vec2 noiseUV = uv * 50.0 + iTime;
    float n = noise(noiseUV);

    // Create color transitions using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(0.6, 1.0, 0.5));
    vec3 color = mix(baseColor, hsl2rgb(vec3(n * 0.7 + 0.3, 1.0, 0.5)), hex);

    // Add turbulence effect
    float turb = turbulent(vec2(a, r)) * 0.5 + 0.5;
    color *= turb;

    return vec4(color, 1.0);
}
