vec4 Effect2(vec2 uv) {
    // Scale and rotate coordinates
    vec2 st = uv * 10.0;

    // Create geometric pattern using polar coordinates
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Add pulsating effect with time
    float pulse = sin(iTime * 3.0 + radius * 5.0) * 0.5 + 0.5;
    radius += pulse * 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3((iTime * 0.5 + angle / (2.0 * PI)) % 1.0, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add noise and turbulence
    float noiseVal = noise(st * 0.5) * 0.5 + 0.5;
    float turb = turbulent(vec3(st * 0.1, iTime)) * 0.5 + 0.5;

    // Combine effects
    vec3 finalColor = mix(rgbColor, vec3(noiseVal), turb);
    finalColor *= smoothstep(9.0, 10.0, radius);

    return vec4(finalColor, 1.0);
}
