vec4 Effect2(vec2 uv) {
    // Distort UV coordinates with sine waves
    vec2 distortedUV = uv + 0.3 * sin(5.0 * uv + iTime);

    // Create radial pattern from center
    vec2 toCenter = distortedUV - 0.5;
    float dist = length(toCenter) * 10.0;
    float angle = atan2(toCenter.y, toCenter.x);

    // Add turbulence for organic feel
    float turb = turbulent(distortedUV * 3.0 + iTime);

    // Create pulsing light effect
    float pulse = sin(iTime * 2.0 + dist) * 0.5 + 0.5;

    // Color shifting based on position and time
    vec3 color = hsl2rgb(vec3(
        (dist + iTime) * 0.1,   // Hue
        0.7 + turb * 0.3,      // Saturation
        pulse                   // Value
    ));

    return vec4(color, 1.0);
}
