vec4 Effect2(vec2 uv) {
    // Rotate UV coordinates by 45 degrees for diagonal pattern
    vec2 rotated = vec2(
        uv.x * cos(pi/4.) - uv.y * sin(pi/4.),
        uv.x * sin(pi/4.) + uv.y * cos(pi/4.)
    );

    // Create moving checkerboard pattern with time-based frequency
    float freq = 100.0;
    float pattern = sin(rotated.x * freq + iTime) +
                   sin(rotated.y * freq + iTime * 0.5);

    // Use threshold to create alternating colors
    vec3 warmColor = hsl2rgb(vec3(0.1, 0.8, 0.6)); // Warm color (orange)
    vec3 coolColor = hsl2rgb(vec3(0.7, 0.8, 0.6)); // Cool color (blue)

    // Create smooth transition between colors
    float mixAmount = sin(iTime * 0.5 + pattern * 10.0) * 0.5 + 0.5;
    vec3 finalColor = mix(warmColor, coolColor, mixAmount);

    return vec4(finalColor, 1.0);
}
