uniform vec2 resolution;
uniform float scale;
void main() {
    vec2 uv = gl_FragCoord.xy / resolution.xy;

    // Scale the UV coordinates
    vec2 scaledUV = uv * scale;

    // Calculate axial coordinates (q, r)
    vec2 axial = floor(scaledUV);

    // Convert axial to Cartesian coordinates
    float x = axial.x - axial.y * 0.5;
    float y = axial.y * sqrt(3.0) / 2.0;

    // Translate back to the original coordinate system
    vec2 center = vec2(x, y) / scale;

    // Calculate distance from current UV to grid center
    float dist = length(uv - center);

    // Draw circles with radius 0.45/scale
    float circle = step(0.45 / scale, dist);

    // Output color based on the circle
    gl_FragColor = vec4(vec3(circle), 1.0);
}
