vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar coordinates
    vec2 pos = uv - 0.5;
    float d = length(pos);
    float angle = atan2(pos.y, pos.x);

    // Add time-based rotation
    angle += iTime * 0.5;

    // Create radial gradient with rotating bands
    float band1 = mod(d * 4.0 + iTime, 1.0);
    float band2 = mod(d * 8.0 + iTime * 0.7, 1.0);
    float band3 = mod(d * 16.0 + iTime * 0.3, 1.0);

    // Create color wheel using HSL colors
    vec3 color1 = hsl2rgb(vec3(0.5 + sin(iTime) * 0.5, 1.0, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.75 + cos(iTime) * 0.5, 1.0, 0.5));
    vec3 color3 = hsl2rgb(vec3(0.25 + sin(iTime * 0.5), 1.0, 0.5));

    // Mix colors based on bands
    vec3 finalColor = mix(color1, color2, band1) * 0.5;
    finalColor += mix(color2, color3, band2) * 0.5;
    finalColor += mix(color3, color1, band3) * 0.5;

    // Add some turbulence for extra movement
    float turb = turbulent(vec2(uv.x * 4.0 + iTime, uv.y * 4.0 + iTime)) * 0.2;
    finalColor *= 1.0 + turb;

    return vec4(finalColor, 1.0);
}
