vec4 Effect2(vec2 uv) {
    // Animate the grid pattern over time
    float time = iTime * 0.5;

    // Create a tiling pattern with diagonal movement
    vec2 st = uv * 10.0 + time;
    st.x += sin(st.y * 0.5 + time) * 2.0;
    st.y += cos(st.x * 0.5 - time) * 2.0;

    // Create diagonal grid lines with movement
    float dx = abs(sin(st.x + st.y)) * 0.5;
    float dy = abs(cos(st.x - st.y)) * 0.5;

    // Add turbulence for shimmer effect
    vec3 noise1 = vec3(turbulent(vec2(st.x, time)), turbulent(vec2(time, st.y)), 0.0);
    vec3 noise2 = vec3(turbulent(vec2(st.y, time)), turbulent(vec2(time, st.x)), 0.0);

    // Combine patterns and add color
    float pattern = (dx + dy) * 0.5;
    pattern += (noise1.x + noise2.x) * 0.3;

    // Create shifting hues using HSL
    vec3 color = hsl(pattern * 0.6 + 0.3, 0.8, 0.6);
    color *= sin(time * 0.5 + pattern * pi) * 0.5 + 0.5;

    // Output the final color with some contrast
    return vec4(color * (1.0 - abs(pattern - 0.5)) * 2.0, 1.0);
}
