vec4 Effect2(vec2 uv) {
    // Create hexagonal grid coordinates
    vec2 st = uv * 5.0; // Scale up for more cells

    // Convert to polar coordinates
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Calculate hexagon tiling using polar coordinates
    float theta = mod(angle + 30.0 * PI / 180.0, 60.0 * PI / 180.0);
    vec2 tile = vec2(radius * cos(theta), radius * sin(theta));

    // Calculate distance from center of hexagon
    float d = length(tile - floor(tile + 0.5));

    // Create pulsing effect using time and sine function
    float pulse = sin(iTime * 3.0) * 0.2;
    float cellSize = 1.0 + pulse;

    // Calculate color based on UV coordinates and time
    vec3 color = hsl(angle / (2.0 * PI), 0.7, 0.5);
    color *= 1.0 - turbulent(vec3(tile.x, tile.y, iTime)) * 0.5;

    // Create final pattern by combining distance field and pulsing
    float alpha = smoothstep(cellSize, cellSize + 0.2, d) *
                  smoothstep(0.0, 1.0, sin(iTime + angle));

    return vec4(color, alpha);
}
