vec4 Effect2(vec2 uv) {
    // Create a pulsing scale factor
    float pulse = sin(iTime * 0.5 + 1.0) * 0.3 + 0.7;

    // Scale and offset coordinates for the network pattern
    vec2 st = uv * 8.0 * pulse;

    // Create a grid of points with noise variation
    vec2 grid = fract(st - 0.5);
    vec2 center = vec2(0.5, 0.5);

    // Calculate distance to neighboring points in four directions
    float d1 = length(grid - center + vec2(-1.0, 0.0));
    float d2 = length(grid - center + vec2(1.0, 0.0));
    float d3 = length(grid - center + vec2(0.0, -1.0));
    float d4 = length(grid - center + vec2(0.0, 1.0));

    // Combine distances and add noise for variation
    float lines = min(d1, d2) + min(d3, d4);
    lines += turbulent(st * 0.5 + iTime) * 0.2;

    // Create a color that shifts with time
    vec3 color = hsl2rgb(vec3(0.6 + iTime * 0.1, 1.0, 0.8));

    // Generate the final effect by combining lines and color
    float glow = max(0.0, 1.0 - length(grid - center) * 4.0);
    vec3 finalColor = mix(vec3(0.0), color, glow);

    return vec4(finalColor, 1.0);
}
