vec4 Effect2(vec2 uv) {
    // Create time-based rotation
    float angle = iTime * 0.5;
    vec2 rotatedUV = uv - 0.5;
    rotatedUV = vec2(
        cos(angle) * rotatedUV.x - sin(angle) * rotatedUV.y,
        sin(angle) * rotatedUV.x + cos(angle) * rotatedUV.y
    );
    rotatedUV += 0.5;
    // Create pattern using cosine waves at different frequencies
    float pattern = 0.0;
    pattern += cos(iTime + length(rotatedUV * 4.0)) * 0.3;
    pattern += cos(iTime + length(rotatedUV * 8.0 - vec2(1,1))) * 0.3;
    pattern += cos(iTime + length(rotatedUV * 16.0 - vec2(2,2))) * 0.3;
    // Convert to RGB with smooth color transitions
    vec3 color = hsl(
        fract((pattern + iTime * 0.1) / 2.0),
        0.7,
        0.5 + pattern * 0.2
    );
    // Add noise for texture
    float noiseValue = noise(rotatedUV * 4.0);
    color *= 1.0 + noiseValue * 0.3;
    return vec4(color, 1.0);
}
