vec4 Effect2(vec2 uv) {
    // Create undulating sine wave pattern with noise
    float wave = sin(uv.x * 4.0 + iTime) * 0.5;
    wave += sin((uv.y * 3.0 - iTime) * 2.0) * 0.3;
    wave += turbulent(vec2(uv * 10.0, iTime)) * 0.2;

    // Create vibrant color shifts using HSL
    vec3 color = hsl2rgb(vec3(
        iTime * 0.5,          // Hue variation over time
        0.8 + wave * 0.2,     // Saturation modulation
        0.6 + sin(iTime) * 0.4 // Lightness modulation
    ));

    return vec4(color, 1.0);
}
