vec4 Effect2(vec2 uv) {
    // Animate UV coordinates over time
    vec2 st = uv * 5.0 + iTime;

    // Create layered noise patterns
    float n1 = noise(st);
    float n2 = turbulent(st * 0.5 + 100.0);
    float n3 = turbulent(st * 0.25 + 200.0);

    // Combine noise layers with different frequencies
    float pattern = (n1 + n2 * 0.5 + n3 * 0.25) * 0.5 + 0.5;

    // Create flowing color shifts using HSL
    vec3 color = hsl(
        sin(st.x * 0.5 + iTime) * 0.5 + 0.5,  // Hue variation
        cos(st.y * 0.5 - iTime) * 0.5 + 0.75,  // Saturation
        pattern                                   // Lightness from noise
    );

    // Add depth with polar coordinates
    vec2 polar = vec2(
        length(uv),
        atan2(uv.y, uv.x)
    );

    // Final color transformation
    color *= hsl2rgb(vec3(
        sin(iTime * 0.5) + 0.5,
        1.0,
        pattern
    ));

    return vec4(color, 1.0);
}
