vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);
    // Create rotating hexagonal pattern
    theta *= 6.0; // Hexagon has six sides
    theta += iTime * 2.0; // Rotate over time

    // Calculate distance from center of hexagons
    float d = abs(sin(theta)) * 0.5 + 0.5;
    d = (1.0 - d) / r;
    // Color transitions using HSL to RGB conversion
    vec3 color = hsl2rgb(vec3(
        mod(r * 100.0 + iTime, 1.0), // Hue variation based on position and time
        0.7, // Saturation
        0.5 + 0.5 * sin(iTime) // Pulsating lightness
    ));
    // Apply noise for additional texture
    float noiseVal = turbulent(vec3(uv * 100.0, iTime));
    color *= 1.0 + noiseVal * 0.2;
    return vec4(color * d, 1.0);
}
