vec4 Effect2(vec2 uv) {
    // Calculate distance from center
    float dist = length(uv);

    // Create pulsating effect using time
    float pulse = sin(iTime * 2.0) * 0.5;

    // Combine distance and pulse for ring pattern
    float ring = mod(dist + pulse, 0.1) * 10.0;

    // Calculate color based on distance and time
    vec3 color = hsl(
        sin(iTime) + dist * 2.0,
        0.7,
        0.5
    );

    // Add pulsing effect to color
    color *= 1.0 + sin(ring * pi + iTime) * 0.5;

    return vec4(color, 1.0);
}
