vec4 Effect2(vec2 uv) {
    // Create a grid of triangles by scaling and tiling the UV coordinates
    vec2 st = fract(uv * 10.0);

    // Calculate triangle rotation using sine and cosine functions
    vec2 center = vec2(0.5, 0.5);
    vec2 offset = st - center;
    float angle = atan(offset.y, offset.x) + sin(iTime) * 0.5;

    // Generate color based on rotating angle and noise
    vec3 color = hsl(angle / (pi * 2.0), 1.0, 0.5);
    color *= mix(0.8, 1.0, noise(vec3(st, iTime)));

    // Add turbulence effect to create dynamic patterns
    float turb = turbulent(vec2(st.x + iTime*0.1, st.y));
    color += vec3(turb * 0.5);

    // Brightness and contrast adjustments
    color = mix(vec3(0.0), color, 1.5);

    return vec4(color, 1.0);
}
