vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 2.0 + iTime;

    // Create two perpendicular sine waves with different frequencies
    float wave1 = sin(st.x * 3.0 - iTime * 2.0);
    float wave2 = cos((st.y + iTime) * 3.0);

    // Combine waves and add turbulence
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(st * 4.0 + vec2(iTime)) * 0.3;

    // Create rotating color gradient using HSL
    vec3 color = hsl(
        mod((st.x + st.y + iTime * 0.5), 1.0),
        0.7,
        0.6
    );

    // Mix colors based on wave pattern
    vec3 finalColor = mix(vec3(0.1, 0.2, 0.3), color, abs(combined) * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
