vec4 Effect2(vec2 uv) {
    // Create a grid of cells based on UV coordinates and time
    vec2 st = uv * 10.0 + iTime;
    vec2 cell = floor(st);
    vec2 frac = fract(st);

    // Add noise to create dynamic variations in the grid
    float noiseVal = noise(vec3(cell, iTime));
    float turbulence = turbulent(vec2(cell) + iTime);

    // Create pulsating shapes using sine waves and time
    float pulse = sin(iTime * 0.5 + cell.x * pi) * 0.5 + 0.5;
    vec3 color = hsl2rgb(vec3(
        (cell.y * 0.1 + noiseVal * 0.2 + iTime * 0.1) % 1.0,
        0.7 + turbulence * 0.3,
        0.5 + pulse * 0.5
    ));

    // Add chromatic aberration effect by offsetting RGB channels
    vec2 uvOffset = vec2(0.005, 0.01);
    vec3 finalColor;
    finalColor.r = texture(iChannel0, uv + uvOffset).r;
    finalColor.g = texture(iChannel0, uv).g;
    finalColor.b = texture(iChannel0, uv - uvOffset).b;

    // Combine effects
    finalColor *= color * (1.0 + turbulence * 0.5);
    return vec4(finalColor, 1.0);
}
