vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to create a grid pattern
    vec2 st = uv * 100.0;

    // Get cell position
    vec2 pos = fract(st);

    // Calculate distance from center of cell
    vec2 center = pos - 0.5;
    float dist = length(center);

    // Create pulsating effect using time and sine function
    float pulse = sin(iTime * 3.0 + turbulent(pos)) * 0.1;
    float radius = 0.4 + pulse;

    // Create grid pattern with pulsing cells
    vec3 color;
    if (dist < radius) {
        // Pulsating white cells
        color = hsl2rgb(vec3(0.0, 0.0, 1.0));
    } else {
        // Black background
        color = hsl2rgb(vec3(0.0, 0.0, 0.0));
    }

    // Add time-based color shift
    float hueShift = iTime * 0.1;
    vec3 finalColor = hsl(hueShift, 1.0, 0.5);

    return vec4(finalColor, 1.0);
}
