vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv - 0.5;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Create pulsing effect with time
    float pulse = sin(iTime * 2.0 + r * 4.0) * 0.5 + 0.5;
    float turbulence = turbulent(vec3(r, iTime, 0)) * 0.1;
    // Combine pulsing and turbulence for dynamic radius
    float finalRadius = r * (pulse + turbulence);
    // Create color shifts using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(0.5 + sin(iTime * 0.1), 1.0, 0.5));
    vec3 accentColor = hsl2rgb(vec3(0.5 + sin(r * 2.0), 1.0, 0.5));
    // Mix colors based on radius and add noise texture
    vec3 color = mix(baseColor, accentColor, smoothstep(0.0, 1.0, finalRadius));
    color *= noise(vec2(st.x * 4.0, st.y * 4.0)) * 0.5 + 0.5;
    return vec4(color, 1.0);
}
