vec4 Effect2(vec2 uv) {
    // Create a grid of cells
    vec2 st = fract(uv * 10.0);
    vec2 center = floor(st * 10.0 + 0.5) / 10.0;

    // Calculate distance from center with rotation
    float time = iTime * 0.5;
    vec2 pos = st - center;
    float angle = sin(time + center.x * pi) + cos(center.y * pi);
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotatedPos = rot * pos;

    // Create circular pattern with turbulence
    float dist = length(rotatedPos);
    float t = turbulent(st * 5.0 + time) * 0.5 + 0.5;
    float circle = step(dist, 0.4 + t * 0.2);

    // Dynamic color shifts using HSL
    vec3 color1 = hsl2rgb(vec3(0.6 + sin(time + center.x * pi) * 0.3, 0.8, 0.5));
    vec3 color2 = hsl2rgb(vec3(0.2 + cos(time + center.y * pi) * 0.3, 0.7, 0.6));
    vec3 finalColor = mix(color1, color2, t);

    // Add noise texture overlay
    vec2 noiseUV = st * 5.0 + time;
    float n = noise(noiseUV) * 0.3;
    finalColor *= 1.0 + n;

    return vec4(finalColor * circle, 1.0);
}
