vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation effect
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan2(st.y, st.x);
    float radius = length(st);

    // Create rotating sine wave pattern with time-based frequency
    float wave = sin(angle + iTime) * 4.0;
    wave += sin(radius * 8.0 - iTime * 3.0);

    // Add noise and turbulence for more dynamic effect
    vec2 noiseUV = uv + vec2(iTime, 0.0);
    float n = noise(noiseUV * 10.0) * 0.5;
    wave += turbulent(vec3(uv * 4.0, iTime)) * 0.3;

    // Create color transitions using HSL
    vec3 color = hsl(wave * 0.2 + 0.5, 0.7, 0.6);
    color = hsl2rgb(color);

    return vec4(color, 1.0);
}
