vec4 Effect2(vec2 uv) {
    // Base color with HSL values
    vec3 color = hsl(0.5, 0.5, 0.5);

    // Calculate distance from center
    vec2 pos = uv - 0.5;
    float dist = length(pos);

    // Create pulsating circles using sine wave and time
    float pulse = sin(iTime * 2.0 + dist * 10.0) * 0.5 + 0.5;
    float scale = 1.0 + pulse * 0.5;

    // Add noise to create dynamic variations
    vec3 noiseVal = vec3(noise(uv * 10.0 + iTime), 
                        noise(uv * 10.0 + iTime + 123.456),
                        noise(uv * 10.0 + iTime + 678.901));

    // Create color transitions using HSL
    vec3 hslColor = hsl(
        sin(iTime * 0.5 + dist * 5.0) * 0.5 + 0.5,   // Hue variation
        0.7 + noiseVal.x * 0.2,                       // Saturation
        0.6 + noiseVal.y * 0.2                        // Lightness
    );

    // Combine colors and apply turbulence
    color = mix(color, hslColor, turbulent(uv * scale + iTime));

    // Add pulsating effect to the final color
    color *= (1.0 - abs(sin(iTime))) * 0.5 + 0.7;

    return vec4(pow(color, vec3(0.5)), 1.0);
}
