vec4 Effect2(vec2 uv) {
    vec2 st = uv;

    // Create rotating hexagonal pattern using polar coordinates
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create hexagonal grid effect
    float hexPattern = sin(angle * 6.0); // 6 points for hexagon
    float distanceFromCenter = abs(radius - 0.5 * (1.0 + sin(iTime))); // Pulsating radius

    // Combine pattern and distance to create final color
    vec3 color = hsl2rgb(vec3(
        0.5 + 0.5 * sin(angle + iTime), // Hue that changes over time
        0.8, // Saturation
        0.5 + 0.5 * (hexPattern * distanceFromCenter) // Lightness based on pattern and distance
    ));

    return vec4(color, 1.0);
}
