vec4 Effect2(vec2 uv) {
    // Create a base frequency for the pattern
    float freq = 5.0;

    // Add some turbulence to create an organic feel
    vec3 p1 = vec3(uv * freq, iTime);
    vec3 p2 = vec3(uv * (freq + 1.0), iTime + 1.0);

    float t1 = turbulent(p1) * 0.5 + 0.5;
    float t2 = turbulent(p2) * 0.5 + 0.5;

    // Combine turbulence values
    float intensity = (t1 + t2) * 0.5;

    // Create a color that shifts over time and space
    vec3 colorHSL = vec3(
        fract(iTime * 0.1 + uv.x * 2.0 + uv.y * 3.0),
        0.7,
        intensity * 0.6 + 0.4
    );

    // Convert to RGB and add some glow
    vec3 color = hsl2rgb(colorHSL);

    // Create a pattern using sine waves with varying frequencies
    float pattern = sin(uv.x * freq * pi) + cos(uv.y * (freq + 1.0) * pi);
    pattern += sin(iTime * 0.5) * 0.5;

    // Normalize pattern to 0-1 range and combine with intensity
    pattern = (pattern * 0.5 + 0.5) * intensity;

    // Final color with glow effect
    vec3 finalColor = color * pattern;

    return vec4(finalColor, 1.0);
}
