vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    float theta = atan2(uv.y, uv.x);
    float r = length(uv);

    // Add time-based rotation
    theta += 3.0 * iTime; // Rotate at 3 radians per second

    // Create interference pattern using sine functions
    float pattern1 = sin(theta) * sin(r * 5.0 + iTime * 2.0);
    float pattern2 = sin(theta * 2.0 + pi) * sin(r * 10.0 - iTime * 3.0);

    // Combine patterns with time modulation
    float intensity = (pattern1 + pattern2) * 0.5;
    intensity *= sin(iTime * 0.5 + r * 2.0); // Add pulsing effect

    // Create color from HSL values
    vec3 color = hsl(
        mod(theta / pi, 2.0), // Hue based on angle
        1.0 - abs(intensity) * 0.5, // Saturation modulation
        0.5 + intensity * 0.5 // Lightness modulation
    );

    return vec4(color, 1.0);
}
