vec4 Effect2(vec2 uv) {
    // Create a position based on UV coordinates and time
    vec3 pos = vec3(uv * 10.0 + sin(iTime), iTime * 5.0);

    // Add some movement to the position
    pos.xy += vec2(sin(pos.z * 0.5) * 2.0, cos(pos.z * 0.5) * 2.0);

    // Convert to polar coordinates for interesting patterns
    vec2 polar = vec2(length(pos.xy), atan2(pos.y, pos.x));

    // Create a base color using HSL
    vec3 color = hsl(polar.y / (pi * 2.0) + iTime * 0.1,
                     0.6 + turbulent(polar) * 0.4,
                     0.5 + sin(iTime) * 0.5);

    // Add some noise and turbulence
    float t = turbulent(pos * 0.5);
    color *= hsv2rgb(vec3(sin(iTime) + polar.y / (pi * 2.0), 1.0, 1.0)) * (0.5 + t * 0.5);

    // Add some depth
    color += vec3(turbulent(pos.xz * 0.5) * 0.5,
                 turbulent(pos.yz * 0.5) * 0.5,
                turbulent(pos.xy * 0.5) * 0.5);

    return vec4(color, 1.0);
}
