vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);

    // Create rotating pattern using sine function
    float rotation = sin(iTime * 0.5) * 0.5 + 0.5;
    theta += sin(theta * 8.0 + iTime) * 0.5;

    // Add turbulence effect
    vec2 distortedUV = uv * (1.0 + turbulent(uv * 4.0 + iTime));

    // Create color transition from blue to green using HSL
    float hue = mix(240.0, 120.0, sin(iTime * 0.5) * 0.5 + 0.5);
    vec3 color = hsl(hue / 360.0, 0.8, 0.5);

    // Combine effects
    return vec4(color, 1.0);
}
