vec4 Effect2(vec2 uv) {
    // Convert to polar coordinates for radial pattern
    float radius = length(uv);
    float angle = atan2(uv.y, uv.x);

    // Create multiple layers of sine waves with varying frequencies
    float layer1 = sin(radius * 16.0 + iTime * 3.0) * 0.5;
    float layer2 = sin(radius * 8.0 + iTime * 2.0) * 0.3;
    float layer3 = sin(radius * 4.0 + iTime) * 0.2;

    // Add noise to create more organic patterns
    vec2 noiseScale = vec2(100.0, 100.0);
    float noiseValue = noise(uv * noiseScale + iTime);

    // Combine layers and add pulsation effect
    float combined = (layer1 + layer2 + layer3) * 0.5 + noiseValue * 0.3;
    combined *= sin(iTime * 0.5) * 0.5 + 0.5; // Pulsation

    // Create color gradient using HSL
    vec3 hslColor = vec3(
        (radius * 0.5 + iTime * 0.1) * pi, // Hue variation
        0.7 + combined * 0.3,             // Saturation
        0.5 + sin(angle + iTime) * 0.2    // Lightness variation
    );

    // Convert to RGB and add glow effect
    vec3 color = hsl2rgb(hslColor);
    color += smoothstep(1.0, 0.0, radius) * 0.5;

    // Add background glow
    vec3 backgroundColor = hsl2rgb(vec3(iTime * 0.2, 0.2, 0.1));
    color = mix(backgroundColor, color, smoothstep(1.0, 0.0, radius) * 0.5);

    return vec4(color, 1.0);
}
