vec4 Effect2(vec2 uv) {
    // Create polar coordinates from UV
    float theta = atan2(uv.y, uv.x);
    float r = length(uv * 1.5); // Scale radius

    // Create rotating color wheel effect
    vec3 colorWheel = hsl2rgb(vec3(theta + iTime, 0.7, 0.5));

    // Create pulsating rings using sine wave and time
    float ringEffect = sin(r * 10.0 - iTime * 2.0) * 0.5 + 0.5;

    // Combine effects with noise for texture
    vec3 finalColor = mix(colorWheel, 
                         hsl2rgb(vec3(theta + iTime*0.5, 0.8, ringEffect)),
                         turbulent(uv * 5.0) * 0.4 + 0.6);

    return vec4(finalColor, 1.0);
}
