vec4 Effect2(vec2 uv) {
    vec2 st = uv * 5.0; // Scale coordinates for more variation
    float turb = turbulent(st + iTime * 0.5); // Add time-based movement

    // Create particle motion using sine and cosine functions
    vec2 motion = vec2(sin(iTime * 0.5 + turb), cos(iTime * 0.3 + turb)) * 0.1;

    // Use noise to generate color variations
    vec3 color = hsl2rgb(vec3(
        fract(turb + iTime) * 360.0, // Hue variation over time
        0.7, // Saturation
        0.5 // Lightness
    ));

    // Add distance attenuation for a more natural look
    float dist = length(uv - vec2(0.5)) * 2.0;
    color *= smoothstep(1.0, 0.0, dist);

    return vec4(color, 1.0);
}
