vec4 Effect2(vec2 uv) {
    // Base color with time-based hue variation
    vec3 baseColor = hsl(uv.x * 5.0 + sin(iTime) * 2.0, 1.0, 0.5);

    // Add noise and turbulence for dynamic variations
    float noiseValue = noise(vec3(uv * 4.0, iTime * 0.5));
    baseColor += vec3(turbulent(vec3(uv * 8.0, iTime)) * 0.2);

    // Create sine wave patterns with time animation
    float sinX = sin(uv.x * pi + iTime) * 0.5 + 0.5;
    float sinY = sin(uv.y * pi + iTime * 0.7) * 0.5 + 0.5;

    // Combine noise and sine waves for final color
    vec3 finalColor = hsl2rgb(baseColor + vec3(sinX, sinY, 0.0));

    return vec4(finalColor, 1.0);
}
