vec4 Effect2(vec2 uv) {
    // Create base river shape with smooth edges
    vec2 pos = uv * 2.0 - 1.0;
    float riverWidth = 0.8;
    float riverHeight = 0.3;

    // Add flowing animation over time
    float flowSpeed = 0.5;
    float flowOffset = sin(iTime * flowSpeed) * 0.5;

    // Create turbulent water effect using noise functions
    vec2 noisePos = pos + vec2(flowOffset, 0.0);
    float turbulenceX = turbulent(noisePos * 10.0 + vec2(0.0, iTime)) * 0.3;
    float turbulenceY = turbulent(noisePos * 10.0 + vec2(iTime, 0.0)) * 0.3;

    // Create color gradient for the river
    vec3 riverColor = hsl(
        (pos.y * 0.5 + 0.5) * 360.0,   // Hue varies vertically
        0.7 + turbulenceX * 0.2,       // Saturation with noise variation
        0.4 + turbulenceY * 0.1        // Lightness with noise variation
    );

    // Add depth to the river by darkening edges
    float distanceFromCenter = length(pos.x) / riverWidth;
    riverColor *= sin(distanceFromCenter * pi + iTime) * 0.5 + 0.5;

    // Create river boundaries and flow effect
    vec2 riverBounds = smoothstep(
        vec2(-riverWidth, -riverHeight), 
        vec2(riverWidth, riverHeight),
        abs(pos)
    );

    // Combine all elements for final output
    return vec4(hsl2rgb(riverColor) * riverBounds.x * riverBounds.y, 1.0);
}
