vec4 Effect2(vec2 uv) {
    // Create a time-varying wave pattern
    vec2 st = uv * 5.0 + vec2(1.0, 1.0); // Scale and offset UV coordinates

    // Add multiple wave layers with different frequencies
    float wave1 = sin(st.x * 2.0 + iTime) * cos(st.y + iTime);
    float wave2 = sin(st.x * 3.0 - iTime) * cos(st.y * 2.0 + iTime);

    // Combine waves and add noise for complexity
    float combined = (wave1 + wave2) * 0.5;
    combined += turbulent(vec2(uv * 4.0 + iTime)) * 0.3; // Add turbulence

    // Create color transitions using HSL
    vec3 color = hsl(
        fract(combined * 0.5 + iTime * 0.1), // Hue variation over time
        0.7, // Saturation
        0.6 + combined * 0.2 // Vary lightness based on wave pattern
    );

    return vec4(color, 1.0);
}
