vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create rotating sine wave pattern with noise
    float wave = sin(theta * 4.0 + turbulent(vec3(theta * 2.0, r * 10.0, iTime)) * 0.5);
    wave += sin(r * 8.0 + iTime * 2.0) * 0.5;

    // Create color transitions
    vec3 color = hsl2rgb(vec3(
        mod(theta * 0.1 + iTime, 1.0), // Hue variation over time and space
        0.7 + turbulent(vec2(r * 4.0, iTime)) * 0.3, // Saturation with noise
        0.5 + wave * 0.5 // Brightness modulation by wave pattern
    ));

    // Add some contrast and brightness
    color = pow(color, vec3(1.2));

    return vec4(color, 1.0);
}
