vec4 Effect2(vec2 uv) {
    // Create base noise pattern with time component
    float t = iTime * 0.5;
    vec3 p = vec3(uv * 10.0, t);

    // Generate multi-scale turbulence
    float turb = turbulent(p * 0.5) * 0.5 +
                 turbulent(p * 2.0 + 1.0f) * 0.3 +
                 noise(vec3(p.x*4.0, p.y*4.0, t)) * 0.2;

    // Create color gradient based on time and position
    vec3 color = hsl(
        sin(t * 0.5 + uv.x * 2.0) * 0.5 + 0.5,
        0.7 + abs(sin(uv.y * 4.0 + t)) * 0.3,
        0.3 + turb * 0.4
    );

    // Add pulsation effect
    float pulse = sin(t) * 0.2 + 1.0;
    color *= pulse;

    // Apply smooth gradient using noise values
    float blend = smoothstep(0.0, 1.0, turb);
    color = mix(vec3(0.0), color, blend);

    return vec4(hsl2rgb(color), 1.0);
}
