vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);

    // Create tessellation pattern using sine waves
    float scale = 5.0 + sin(r * 3.0 + iTime) * 2.0;
    vec2 grid = floor((st * scale) + 0.5);

    // Calculate triangle divisions
    vec2 pos = fract((st * scale) + 0.5);
    float tri = sin(a + r * 10.0 + iTime * 2.0) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        fract(r * 0.2 + a * 0.5 + iTime * 0.1),
        0.7 + sin(r * 4.0 + iTime) * 0.3,
        0.3 + cos(r * 4.0 + iTime) * 0.7
    );

    // Convert to RGB and add noise
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 1.0 + noise(vec3(uv * 10.0, iTime)) * 0.5;

    // Final color with alpha
    return vec4(rgbColor, 1.0);
}
