vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern
    float t = iTime * 0.5;

    // Create a circular pattern with noise
    vec2 st = uv * 2.0 - 1.0;
    float d = length(st);
    float circle = smoothstep(0.8, 1.0, d);

    // Add turbulence to create dynamic texture
    vec3 noisePos = vec3(uv * 5.0 + t, t);
    float turb = turbulent(noisePos) * 0.5 + 0.5;

    // Create color transitions using HSL
    vec3 hslColor = vec3(t * 2.0, 1.0, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add geometric elements with rotating patterns
    float angle = atan2(st.y, st.x) + t;
    float sine = sin(angle * 4.0 + t * 2.0);
    float line = smoothstep(0.95, 1.0, abs(sine));

    // Combine all elements
    vec3 finalColor = mix(rgbColor * turb, vec3(circle), line);

    return vec4(finalColor, 1.0);
}
