vec4 Effect2(vec2 uv) {
    // Create rotating sine wave patterns
    float time = iTime * 0.5;

    // Rotate coordinates over time
    vec2 st = uv - 0.5;
    float theta = atan2(st.y, st.x) + time;
    float dist = length(st);

    // Create RGB sine waves with different phases and frequencies
    vec3 color = vec3(
        sin(theta + time * 2.0 + dist * 2.0) * 0.5 + 0.5,
        sin(theta - time * 1.0 + dist * 1.0) * 0.5 + 0.5,
        sin(theta + time * 3.0 - dist * 2.0) * 0.5 + 0.5
    );

    // Add turbulence for more dynamic effect
    vec3 noise = vec3(
        turbulent(vec3(uv.x, time, 0.0)),
        turbulent(vec3(uv.y, time, 1.0)),
        turbulent(vec3(uv.x + uv.y, time, 2.0))
    );

    // Combine color and noise
    color = mix(color, noise, 0.5);

    return vec4(color, 1.0);
}
