vec4 Effect2(vec2 uv) {
    // Convert UV to [-1,1] range
    vec2 st = uv * 2.0 - 1.0;

    // Add some movement over time
    float time = iTime * 0.5;

    // Create a rotating pattern using polar coordinates
    float radius = length(st);
    float angle = atan2(st.y, st.x) + time;

    // Add noise to create turbulence effect
    vec2 noisePos = st * 5.0 + vec2(time);
    float turb = turbulent(noisePos);

    // Create color variations using HSL
    vec3 hslColor;
    hslColor.x = fract(angle * 0.1 + time) * 360.0; // Hue variation
    hslColor.y = 0.7 + turb * 0.3; // Saturation with noise
    hslColor.z = 0.5 + radius * 0.5; // Lightness based on distance

    // Convert HSL to RGB and return
    return vec4(hsl2rgb(hslColor), 1.0);
}
