vec4 Effect2(vec2 uv) {
    // Create time-varying sine wave patterns with different frequencies
    float t = iTime * 0.5;

    // Interweaving pattern using multiple sine waves
    vec3 color1 = sin(uv.xyy * 8.0 + t * 4.0);
    vec3 color2 = sin(uv.yxx * 6.0 + t * 3.0 + 2.0);

    // Mix colors with phase shifts for RGB transitions
    vec3 mixedColor = mix(color1, color2, 0.5);

    // Add noise and turbulence to create dynamic movement
    vec2 noisePos = uv * 4.0 + t;
    float noiseVal = noise(noisePos) * 0.5 + 0.5;

    // Create HSL color transition effect
    vec3 hslColor = vec3(
        mod(t * 0.1, 1.0),   // Hue cycling over time
        0.7,                 // Saturation
        noiseVal             // Brightness modulation with noise
    );

    // Convert HSL to RGB and mix with sine wave colors
    vec3 finalColor = hsl2rgb(hslColor) * (mixedColor * 0.5 + 0.5);

    return vec4(finalColor, 1.0);
}
