vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);
    float time = iTime * 0.5;

    // Create a grid of points
    vec2 grid = floor(uv * 50.0);
    vec2 pos = fract(uv * 50.0) - 0.5;

    // Add noise to position for randomness
    vec2 offset = noise(vec3(grid, time)) * 0.4;
    pos += offset;

    // Calculate distance from center
    float dist = length(pos);

    // Create pulsing effect with color transitions
    float hue = fract(time + grid.x * 0.1 + grid.y * 0.2) * pi;
    vec3 col = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add glow effect
    float glow = exp(-dist * 5.0);
    color += col * glow;

    // Add noise to create variations in brightness
    color *= noise(vec3(uv * 10.0 + time, 0.0)) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
