vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern with noise
    vec3 noise1 = turbulent(vec3(uv * 5.0 + iTime, 0.0));
    vec3 noise2 = turbulent(vec3(uv * 10.0 - iTime, 0.0));

    // Combine noise patterns and add time variation
    float d = (noise1.x + noise2.x) * 0.5;
    d += sin(iTime * 0.5) * 0.5;

    // Create color variations using HSL to RGB conversion
    vec3 hslColor = vec3(
        iTime * 0.1 + uv.x * 2.0,   // Hue variation over time and space
        0.7,                        // Saturation
        0.5 + d * 0.5               // Lightness modulation by noise
    );

    // Convert HSL to RGB and mix with black background
    vec3 color = hsl2rgb(hslColor);
    color *= smoothstep(0.0, 1.0, d + 0.5);

    return vec4(color, 1.0);
}
