vec4 Effect2(vec2 uv) {
    // Translate UV coordinates to center and scale appropriately
    vec2 st = uv * 2.0 - 1.0;

    // Convert to polar coordinates
    float theta = atan2(st.y, st.x);
    float r = length(st);

    // Create rotating pattern using time
    theta += iTime * 3.0; // Speed up rotation

    // Add pulsing effect with sine wave
    r *= sin(iTime) + 1.5;

    // Use noise to create turbulence in the pattern
    float noiseVal = turbulent(vec2(theta, r));

    // Create color wheel using HSL colors
    vec3 color = hsl2rgb(vec3(
        theta * 0.5 + iTime * 0.1,
        1.0,
        0.5 + noiseVal * 0.5
    ));

    return vec4(color, 1.0);
}
