vec4 Effect2(vec2 uv) {
    // Translate UV to center coordinates
    vec2 st = uv - 0.5;

    // Add rotation based on time
    float theta = atan2(st.y, st.x);
    float r = length(st);
    theta += iTime * 0.5; // Rotation speed

    // Create sine wave patterns with time offset
    float wave1 = sin(r * 8.0 + theta * 4.0 + iTime) * 0.5 + 0.5;
    float wave2 = sin(theta * 3.0 - r * 6.0 + iTime * 2.0) * 0.5 + 0.5;

    // Combine waves with turbulence
    float noiseVal = turbulent(vec2(st.x * 4.0, st.y * 4.0)) * 0.5 + 0.5;
    float pattern = (wave1 + wave2) * noiseVal;

    // Create color transitions using HSL
    vec3 baseColor = hsl2rgb(vec3(iTime * 0.1, 1.0, 0.8));
    vec3 noiseColor = hsl2rgb(vec3((iTime * 0.1 + noiseVal) * 2.0, 1.0, 0.6));

    // Mix colors based on pattern
    vec3 finalColor = mix(baseColor, noiseColor, pattern);

    return vec4(finalColor, 1.0);
}
