vec4 Effect2(vec2 uv) {
    // Rotate coordinates over time
    vec2 st = uv - 0.5;
    float angle = sin(iTime * 0.5) + pi/2;
    mat2 rot = mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
    vec2 rotated = rot * st;
    // Create geometric pattern using polar coordinates
    float d = length(rotated);
    float theta = atan2(rotated.y, rotated.x);

    // Add time-based pulsing and movement
    float pulse = sin(iTime) * 0.5 + 0.5;
    float shape = sin(theta * 16.0 + iTime) * 0.5 + 0.5;
    shape *= sin(d * 8.0 - iTime);

    // Create color transitions
    vec3 color = hsl2rgb(vec3(
        (theta / pi + pulse) * 0.5,
        1.0,
        0.6 + 0.4 * sin(iTime)
    ));

    // Add noise texture for extra detail
    vec2 noiseUV = rotated * 8.0 + iTime;
    float n = noise(noiseUV);

    // Combine elements and output
    return vec4(color * (shape + n * 0.3), 1.0) 
         * (1.0 - sin(d * 8.0 - iTime/2.0) * 0.5);
}
