vec4 Effect2(vec2 uv) {
    // Create hexagonal grid coordinates
    vec2 q = uv * 0.7;
    float r = sin(iTime * 0.5 + q.x * pi);
    float s = cos(iTime * 0.5 + q.y * pi);

    // Pulsating effect using sine wave
    float pulse = sin(iTime) * 0.5 + 0.5;
    vec2 st = uv * (1.0 + pulse * 0.3);

    // Add noise and turbulence for dynamic patterns
    float noiseVal = noise(st * 4.0 + iTime);
    float turb = turbulent(st * 8.0 + iTime);

    // Calculate distance from center for radial gradient
    vec2 center = st - vec2(0.5, 0.5);
    float dist = length(center) * 1.5;

    // Create color shifts using HSL to RGB conversion
    vec3 color = hsl(dist + iTime, 0.7, 0.6);
    color = hsl2rgb(color);

    // Combine with noise and turbulence for final effect
    color *= (1.0 - noiseVal * 0.5) + turb * 0.3;

    return vec4(color, 1.0);
}
