vec4 Effect2(vec2 uv) {
    // Base time factor
    float time = iTime * 0.5;

    // Rotate UV coordinates over time
    vec2 rotatedUv = vec2(
        uv.x * cos(time) - uv.y * sin(time),
        uv.x * sin(time) + uv.y * cos(time)
    );

    // Create a base color using HSL to RGB conversion
    vec3 baseColor = hsl2rgb(vec3(0.5 * time, 1.0, 0.5));

    // Add noise and turbulence patterns
    float noisePattern = turbulent(rotatedUv * 4.0) * 0.5 + 0.5;
    float noiseDetail = turbulent(vec3(rotatedUv * 8.0, time)) * 0.5 + 0.5;

    // Combine colors and patterns
    vec3 finalColor = mix(
        baseColor,
        hsl2rgb(vec3(0.5 * time + noisePattern, 1.0, 0.5)),
        noiseDetail * 0.7
    );

    // Add flowing movement using sine functions
    finalColor *= sin(time + uv.x * 4.0) * 0.5 + 0.5;
    finalColor += vec3(sin(time + uv.y * 4.0)) * 0.2;

    return vec4(finalColor, 1.0);
}
