vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Create orbital pattern with noise modulation
    float orbit = sin(theta * 4.0 + iTime) * 0.5 + 0.5;
    orbit *= r * 10.0;
    orbit += noise(vec2(theta, iTime)) * 0.3;

    // Add turbulence for chaotic effect
    vec2 noiseUV = uv * 10.0 + iTime;
    float turb = turbulent(noiseUV);

    // Create color based on orbital position and noise
    vec3 color = hsl2rgb(vec3(
        (theta / pi + 0.5) * 360.0,
        0.7 + turb * 0.3,
        0.5 + sin(iTime * 0.5 + theta) * 0.5
    ));

    // Add some orbital glow effect
    float glow = smoothstep(0.8, 1.0, r);
    color *= mix(vec3(1.0), vec3(0.2, 0.6, 1.0), glow);

    return vec4(color * (orbit + turb * 0.5), 1.0);
}
