vec4 Effect2(vec2 uv) {
    vec2 timeUv = uv * 0.1 + iTime * 0.5;

    // Base noise layer
    float n1 = noise(uv);
    float n2 = noise(timeUv);

    // Add multiple noise layers with different scales and offsets
    float noiseValue = n1 * 0.7 + n2 * 0.3;

    // Apply turbulence for more complex patterns
    noiseValue += turbulent(vec2(uv.x * 1.5, uv.y * 1.5)) * 0.4;

    // Convert to HSL and adjust colors
    vec3 color = hsl2rgb(hsv2rgb(vec3(
        noiseValue * 0.5 + 0.5,   // Hue variation
        0.8 + noiseValue * 0.2,  // Saturation
        0.4 + noiseValue * 0.1   // Value
    )));

    return vec4(color, 1.0);
}
