//prompt: something wonderful 
vec4 Effect2(vec2 uv) {
    // Create a circular pattern with time-based oscillations
    vec2 p = uv * 2.0 - 1.0;
    float d = length(p);

    // Add noise and animation
    float t = iTime * 0.25;
    vec3 pos = vec3(p.x, p.y, t);
    float cell = noise(pos * 0.5 + sin(t) * 0.1) *
                noise(pos * 2.0 - cos(t * 2.0) * 0.2) +
                noise(pos * 4.0 + t) * 0.5;

    // Create interference pattern
    float interf = noise(vec3(p.x * 0.7 - t, p.y * 0.7 + t * 0.5, 0.0)) * 2.0 -
                   noise(vec3(p.x * 0.9 + t * 0.5, p.y * 0.9 - t * 0.3, 1.0)) * 2.0;

    // Convert to color
    vec3 color = hsl(abs(cell * 0.5 + interf) * 7.0,
                     sin(t * 2.0 + d * 3.0) * 0.5 + 0.5,
                     cos(t * 3.0 - d * 4.0) * 0.5 + 0.5);

    // Add distance falloff
    color *= 1.0 - d * 0.3;
    color = mix(color, vec3(0.0), max(0.0, 1.0 - d));

    return vec4(color, 1.0);
}
